def pickme_spletny():
    template = random.choice(gossip_templates)
    name1 = random.choice(girl_names + boy_names)
    name2 = random.choice(girl_names + boy_names)
    name3 = random.choice(girl_names + boy_names)
    while name2 == name1:
        name2 = random.choice(girl_names + boy_names)
    while name3 == name1 or name3 == name2:
        name3 = random.choice(girl_names + boy_names)
    
    msg = template.format(name=name1, name2=name2, name3=name3)
    return msg.lower()


